package main

import (
	"fmt"
	"os"
	"io/ioutil"
)

func main() {
	data, err := ioutil.ReadFile("hello.txt")
	if err != nil {
		fmt.Fprintf(os.Stderr, "Error reading file: %v\n", err)
		os.Exit(1)
	}
	fmt.Print(string(data))
}

